import React, {Component} from 'react';

class ListarUsuario extends /*React.*/ Component{
//equivalente al ngOnInit. El componente se ha montado
    componentDidMount(){
     //cuando se renderice el componente se ejecutará lo de abajo 
        let promesaHTTP = window.fetch('http://127.0.0.1:4000/api/usuarios/');
        promesaHTTP.then((resHTTP) => {     //las funciones => hacen el bind a ese this
            let promesaJSON = resHTTP.json();
            promesaJSON.then((objColeccionUsu)=> {
                console.log( JSON.stringify(objColeccionUsu));
                this.setState({
                    listaUsuarios: objColeccionUsu
                });
            });     
        }); 
    }

    componentWillMount(){
    }

    render() {
    return (
        <div>
            <h2>Lista de usuarios</h2>

            <table>
                <tr> 
                    <th>Nombre</th>
                    <th>Email</th>
                </tr>
                <tr> 
                    <th>{ this.state.listaUsuarios[0].nombre }</th>
                    <th>{ this.state.listaUsuarios[0].email }</th>

                </tr>
                <tr> 
                    <th>Celda1</th>
                    <th>Celda2</th>
                    <th>Celda3</th>

                </tr>

            </table>
        </div>

    );
}
    }


export default ListarUsuario;
